<?phpfunction webnus_testimonial ($atts, $content = null) { 	extract(shortcode_atts(array( 		'img'=>'',	'name'=>'',	'subtitle' => '',	), $atts));			$out = '';			$out .= '<div class="testimonial">';	$out .= '<div class="testimonial-content">';	$out .= '<h4>'. do_shortcode($content) .'</h4>';	$out .= '<div class="testimonial-arrow"></div>';	$out .= '</div>';	$out .= '<div class="testimonial-brand"> <img src="'. $img .'" alt="">';	$out .= '<h5><strong>'. $name .'</strong><br>';	$out .= '<em>'. $subtitle .'</em></h5>';	$out .= '</div>';	$out .= '</div>';return $out;} add_shortcode('testimonial','webnus_testimonial');?>